                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.boot.builder.SpringApplicationBuilder;
import org.zbus.mq.server.MqAdaptor;
import org.zbus.mq.server.MqServer;
import org.zbus.mq.server.MqServerConfig;
import org.zbus.proxy.thrift.ThriftCodec;

@SpringBootApplication
public class StartZbusServiceAPP {

	public static void main(String[] args) throws InterruptedException {
		// TODO Auto-generated method stub
		doStartHandler();
		new SpringApplicationBuilder(StartZbusServiceAPP.class).web(false).run(args);
		CountDownLatch closeLatch = new CountDownLatch(1);
		closeLatch.await();
	}
	
    /**
     * 启动MQ消息服务
     */
    public static void doStartHandler(){
    	MqServerConfig config = new MqServerConfig();
        config.serverHost = "127.0.0.1";
        config.serverPort = 15555;
        config.thriftServer= null;
        config.trackServerList=null;

        MqServer server = new MqServer(config);
        if (config.thriftServer != null) {
          MqAdaptor thriftAdaptor = new MqAdaptor(server);
          thriftAdaptor.codec(new ThriftCodec());
          server.registerAdaptor(config.thriftServer, thriftAdaptor, "Thrift");
        }
        try {
			server.start();
		} catch (IOException e) {
		}
    }
}
